import React, { useState } from "react";
import { auth } from "./firebase";
import "./Login.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const signInHandle = (e) => {
    e.preventDefault();
  };

  const registerHandle = (e) => {
    e.preventDefault();
    auth
      .createUserWithEmailAndPassword(email, password)
      .then((auth) => {
        console.log(auth);
      })
      .cactch((error) => alert(error.message));
  };

  return (
    <div className="login-page">
      <img
        className="login_logo"
        src="https://bizmonthly.com/wp-content/uploads/2020/04/Amazon-logo-black-template.png"
      />

      <div className="login-container">
        <h1>SIGN IN</h1>
        <form>
          <h5>E-mail</h5>
          <input
            required
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <h5>Password</h5>
          <input
            required
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <button
            type="submit"
            onClick={signInHandle}
            className="login-signin-button"
          >
            LETS GO!
          </button>
          <button onClick={registerHandle} className="login-create-button">
            CREATE AMAZON ACCOUNT
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
